// QRCode.js fallback - minimal implementation
(function() {
    // Simple QR code placeholder implementation
    window.QRCode = function(element, options) {
        if (!element) return;
        
        const text = options.text || options;
        const size = options.width || 200;
        
        // Create a simple placeholder with text instead of actual QR code
        element.innerHTML = `
            <div style="
                width: ${size}px; 
                height: ${size}px; 
                background: white; 
                display: flex; 
                flex-direction: column;
                align-items: center; 
                justify-content: center; 
                border: 2px solid #000;
                font-family: monospace;
                text-align: center;
                padding: 20px;
                box-sizing: border-box;
            ">
                <div style="font-size: 12px; font-weight: bold; margin-bottom: 10px;">QR Code</div>
                <div style="font-size: 10px; word-break: break-all; line-height: 1.2;">
                    ${text.length > 100 ? text.substring(0, 100) + '...' : text}
                </div>
            </div>
        `;
    };
    
    // Add CorrectLevel constants
    window.QRCode.CorrectLevel = {
        L: 1,
        M: 0, 
        Q: 3,
        H: 2
    };
})();